import React, { useState, useEffect } from 'react';
import { friendAPI } from '../services/api';
import { socketEvents } from '../services/socket';
import { getSocket } from '../services/socket';

const FriendManager = () => {
  const [searchKeyword, setSearchKeyword] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [friends, setFriends] = useState([]);
  const [friendRequests, setFriendRequests] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadFriends();
    loadFriendRequests();
    setupSocketListeners();
  }, []);

  const setupSocketListeners = () => {
    const socket = getSocket();
    if (!socket) return;

    socket.on(socketEvents.FRIEND_REQUEST, (request) => {
      setFriendRequests(prev => [...prev, request]);
    });

    socket.on(socketEvents.FRIEND_REQUEST_ACCEPTED, (friend) => {
      setFriends(prev => [...prev, friend]);
      setFriendRequests(prev => prev.filter(req => req.id !== friend.id));
    });

    socket.on(socketEvents.FRIEND_REQUEST_REJECTED, (requestId) => {
      setFriendRequests(prev => prev.filter(req => req.id !== requestId));
    });

    socket.on(socketEvents.FRIEND_REMOVED, (friendId) => {
      setFriends(prev => prev.filter(friend => friend.id !== friendId));
    });
  };

  const loadFriends = async () => {
    try {
      const response = await friendAPI.getFriends();
      if (response.success) {
        setFriends(response.friends);
      }
    } catch (error) {
      console.error('Error loading friends:', error);
    }
  };

  const loadFriendRequests = async () => {
    try {
      const response = await friendAPI.getFriendRequests();
      if (response.success) {
        setFriendRequests(response.received);
      }
    } catch (error) {
      console.error('Error loading friend requests:', error);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchKeyword.length < 2) return;

    setLoading(true);
    try {
      const response = await friendAPI.searchUsers(searchKeyword);
      if (response.success) {
        setSearchResults(response.users);
      }
    } catch (error) {
      console.error('Error searching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSendFriendRequest = async (userId) => {
    try {
      const response = await friendAPI.sendFriendRequest(userId);
      if (response.success) {
        setSearchResults(prev => prev.filter(user => user.id !== userId));
        if (response.autoAccepted) {
          loadFriends();
        } else {
          loadFriendRequests();
        }
      }
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };

  const handleAcceptRequest = async (requestId) => {
    try {
      const response = await friendAPI.acceptFriendRequest(requestId);
      if (response.success) {
        loadFriends();
        loadFriendRequests();
      }
    } catch (error) {
      console.error('Error accepting friend request:', error);
    }
  };

  const handleRejectRequest = async (requestId) => {
    try {
      const response = await friendAPI.rejectFriendRequest(requestId);
      if (response.success) {
        loadFriendRequests();
      }
    } catch (error) {
      console.error('Error rejecting friend request:', error);
    }
  };

  const handleRemoveFriend = async (friendId) => {
    try {
      const response = await friendAPI.removeFriend(friendId);
      if (response.success) {
        loadFriends();
      }
    } catch (error) {
      console.error('Error removing friend:', error);
    }
  };

  return (
    <div className="friend-manager">
      <div className="search-section">
        <form onSubmit={handleSearch}>
          <input
            type="text"
            value={searchKeyword}
            onChange={(e) => setSearchKeyword(e.target.value)}
            placeholder="Tìm kiếm theo tên đăng nhập hoặc họ tên..."
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Đang tìm...' : 'Tìm kiếm'}
          </button>
        </form>
      </div>

      <div className="search-results">
        {searchResults.map(user => (
          <div key={user.id} className="user-item">
            <div className="user-info">
              <img src={user.avatar || '/default-avatar.png'} alt={user.fullname} />
              <div>
                <div className="username">{user.username}</div>
                <div className="fullname">{user.fullname}</div>
              </div>
            </div>
            <div className="user-actions">
              {user.relationship === 'friend' ? (
                <>
                  <button onClick={() => handleRemoveFriend(user.id)}>Xóa bạn</button>
                </>
              ) : user.relationship === 'sent_request' ? (
                <button disabled>Đã gửi lời mời</button>
              ) : user.relationship === 'received_request' ? (
                <>
                  <button onClick={() => handleAcceptRequest(user.request_id)}>Chấp nhận</button>
                  <button onClick={() => handleRejectRequest(user.request_id)}>Từ chối</button>
                </>
              ) : (
                <button onClick={() => handleSendFriendRequest(user.id)}>Kết bạn</button>
              )}
            </div>
          </div>
        ))}
      </div>

      <div className="friend-requests">
        <h3>Lời mời kết bạn</h3>
        {friendRequests.map(request => (
          <div key={request.id} className="request-item">
            <div className="user-info">
              <img src={request.avatar || '/default-avatar.png'} alt={request.fullname} />
              <div>
                <div className="username">{request.username}</div>
                <div className="fullname">{request.fullname}</div>
              </div>
            </div>
            <div className="request-actions">
              <button onClick={() => handleAcceptRequest(request.id)}>Chấp nhận</button>
              <button onClick={() => handleRejectRequest(request.id)}>Từ chối</button>
            </div>
          </div>
        ))}
      </div>

      <div className="friends-list">
        <h3>Danh sách bạn bè</h3>
        {friends.map(friend => (
          <div key={friend.id} className="friend-item">
            <div className="user-info">
              <img src={friend.avatar || '/default-avatar.png'} alt={friend.fullname} />
              <div>
                <div className="username">{friend.username}</div>
                <div className="fullname">{friend.fullname}</div>
                <div className={`status ${friend.status}`}>{friend.status}</div>
              </div>
            </div>
            <div className="friend-actions">
              <button onClick={() => handleRemoveFriend(friend.id)}>Xóa bạn</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FriendManager;
