import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  TextField,
  IconButton,
  Typography,
  Badge,
  Tabs,
  Tab,
  Button,
} from '@mui/material';
import {
  Send as SendIcon,
  AttachFile as AttachFileIcon,
  EmojiEmotions as EmojiIcon,
  Search as SearchIcon,
  Logout as LogoutIcon,
} from '@mui/icons-material';
import EmojiPicker from 'emoji-picker-react';
import io from 'socket.io-client';
import { userAPI, friendAPI, chatAPI } from '../services/api';

const Chat = () => {
  const navigate = useNavigate();
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [friends, setFriends] = useState([]);
  const [friendRequests, setFriendRequests] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [activeTab, setActiveTab] = useState(0);
  const [currentChat, setCurrentChat] = useState(null);
  const [messageInput, setMessageInput] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    const newSocket = io('http://localhost:5000', {
      auth: { token },
    });

    setSocket(newSocket);

    return () => newSocket.close();
  }, [navigate]);

  useEffect(() => {
    if (socket) {
      socket.on('message', (message) => {
        setMessages((prev) => [...prev, message]);
      });

      socket.on('friendRequest', (request) => {
        setFriendRequests((prev) => [...prev, request]);
      });
    }
  }, [socket]);

  useEffect(() => {
    const fetchFriends = async () => {
      try {
        const response = await friendAPI.getFriends();
        if (response.data && response.data.friends && Array.isArray(response.data.friends)) {
          setFriends(response.data.friends);
        } else {
          console.error('Invalid friends data format:', response.data);
          setFriends([]);
        }
      } catch (error) {
        console.error('Error fetching friends:', error);
        setFriends([]);
      }
    };

    fetchFriends();
  }, []);

  useEffect(() => {
    const fetchFriendRequests = async () => {
      try {
        const response = await friendAPI.getFriendRequests();
        console.log('Friend requests:', response.data);
        if (response.data && response.data.success) {
          // Check if received is an object and convert it to an array
          if (response.data.received) {
            if (Array.isArray(response.data.received)) {
              setFriendRequests(response.data.received);
            } else if (typeof response.data.received === 'object') {
              // Convert object to array of values
              const receivedArray = Object.values(response.data.received);
              setFriendRequests(receivedArray);
            } else {
              console.error('Invalid friend requests format:', response.data);
              setFriendRequests([]);
            }
          } else {
            setFriendRequests([]);
          }
        } else {
          console.error('Failed to fetch friend requests:', response.data);
          setFriendRequests([]);
        }
      } catch (error) {
        console.error('Error fetching friend requests:', error);
        setFriendRequests([]);
      }
    };

    if (socket) {
      fetchFriendRequests();

      // Listen for new friend requests
      socket.on('friendRequest', (request) => {
        console.log('New friend request received:', request);
        setFriendRequests((prev) => [...prev, request]);
      });

      return () => {
        socket.off('friendRequest');
      };
    }
  }, [socket]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = () => {
    if (messageInput.trim() && currentChat) {
      socket.emit('message', {
        to: currentChat._id,
        content: messageInput,
      });
      setMessageInput('');
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (file && currentChat) {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('to', currentChat._id);

      try {
        await chatAPI.uploadFile(formData);
      } catch (error) {
        console.error('Error uploading file:', error);
      }
    }
  };

  const handleSearch = async () => {
    try {
      const response = await userAPI.searchUsers(searchQuery);
      console.log('Search response:', response.data);

      if (response.data && response.data.users) {
        // Handle both single object and array cases
        if (Array.isArray(response.data.users)) {
          setSearchResults(response.data.users);
        } else if (typeof response.data.users === 'object' && response.data.users !== null) {
          // If it's a single object, convert to array with one item
          setSearchResults([response.data.users]);
        } else {
          console.error('Invalid search results format:', response.data);
          setSearchResults([]);
        }
      } else {
        console.error('Invalid search results format:', response.data);
        setSearchResults([]);
      }

      setActiveTab(2);
    } catch (error) {
      console.error('Error searching users:', error);
      setSearchResults([]);
    }
  };

  const handleSendFriendRequest = async (userId) => {
    try {
      console.log('Sending friend request to user ID:', userId);
      await friendAPI.sendFriendRequest(userId);
      setSearchResults((prev) => prev.filter((u) => u.id !== userId));
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };

  const handleAcceptFriendRequest = async (requestId) => {
    try {
      await friendAPI.acceptFriendRequest(requestId);
      setFriendRequests((prev) => prev.filter((req) => req._id !== requestId));
    } catch (error) {
      console.error('Error accepting friend request:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <Box sx={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <Drawer
        variant="permanent"
        sx={{
          width: 300,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: 300,
            boxSizing: 'border-box',
          },
        }}
      >
        <Box sx={{ p: 2 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6">Zalo Clone</Typography>
            <IconButton onClick={handleLogout}>
              <LogoutIcon />
            </IconButton>
          </Box>
          <Box sx={{ display: 'flex', mb: 2 }}>
            <TextField
              size="small"
              placeholder="Tìm kiếm..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              sx={{ flexGrow: 1, mr: 1 }}
            />
            <IconButton onClick={handleSearch}>
              <SearchIcon />
            </IconButton>
          </Box>
          <Tabs value={activeTab} onChange={(e, newValue) => setActiveTab(newValue)}>
            <Tab label="Bạn bè" />
            <Tab label="Lời mời" />
            <Tab label="Tìm kiếm" />
          </Tabs>
        </Box>
        <List>
          {activeTab === 0 && friends.map((friend) => (
            <ListItem
              key={friend?._id || `friend-${Math.random()}`}
              button
              onClick={() => friend?._id && setCurrentChat(friend)}
              selected={currentChat?._id === friend?._id}
            >
              <ListItemAvatar>
                <Badge
                  color="success"
                  variant="dot"
                  invisible={!friend?.isOnline}
                >
                  <Avatar src={friend?.avatar || ''} />
                </Badge>
              </ListItemAvatar>
              <ListItemText
                primary={friend?.fullname || 'Unknown Friend'}
                secondary={friend?.isOnline ? 'Online' : 'Offline'}
              />
            </ListItem>
          ))}
          {activeTab === 1 && friendRequests.map((request) => (
            <ListItem key={request._id || `req-${Math.random()}`}>
              <ListItemAvatar>
                <Avatar src={request.sender?.avatar || ''} />
              </ListItemAvatar>
              <ListItemText
                primary={request.sender?.fullname || 'Unknown User'}
                secondary="Đã gửi lời mời kết bạn"
              />
              <Button
                size="small"
                variant="contained"
                onClick={() => handleAcceptFriendRequest(request._id)}
                disabled={!request._id}
              >
                Chấp nhận
              </Button>
            </ListItem>
          ))}
          {activeTab === 2 && searchResults.map((user) => (
            <ListItem key={user?.id || `user-${Math.random()}`}>
              <ListItemAvatar>
                <Avatar src={user?.avatar || ''} />
              </ListItemAvatar>
              <ListItemText primary={user?.fullname || 'Unknown User'} />
              <Button
                size="small"
                variant="contained"
                onClick={() => handleSendFriendRequest(user?.id)}
                disabled={!user?.id}
              >
                Kết bạn
              </Button>
            </ListItem>
          ))}
        </List>
      </Drawer>

      {/* Main chat area */}
      <Box sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column' }}>
        {currentChat ? (
          <>
            <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
              <Typography variant="h6">{currentChat.fullname}</Typography>
            </Box>
            <Box sx={{ flexGrow: 1, overflow: 'auto', p: 2 }}>
              {messages.map((message, index) => (
                <Box
                  key={index}
                  sx={{
                    display: 'flex',
                    justifyContent: message.sender === currentChat._id ? 'flex-start' : 'flex-end',
                    mb: 2,
                  }}
                >
                  <Box
                    sx={{
                      maxWidth: '70%',
                      p: 2,
                      borderRadius: 2,
                      bgcolor: message.sender === currentChat._id ? 'grey.100' : 'primary.main',
                      color: message.sender === currentChat._id ? 'text.primary' : 'white',
                    }}
                  >
                    {message.content}
                  </Box>
                </Box>
              ))}
              <div ref={messagesEndRef} />
            </Box>
            <Box sx={{ p: 2, borderTop: 1, borderColor: 'divider' }}>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <IconButton onClick={() => setShowEmojiPicker(!showEmojiPicker)}>
                  <EmojiIcon />
                </IconButton>
                <input
                  type="file"
                  id="fileInput"
                  style={{ display: 'none' }}
                  onChange={handleFileUpload}
                />
                <label htmlFor="fileInput">
                  <IconButton component="span">
                    <AttachFileIcon />
                  </IconButton>
                </label>
                <TextField
                  fullWidth
                  variant="outlined"
                  placeholder="Nhập tin nhắn..."
                  value={messageInput}
                  onChange={(e) => setMessageInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  sx={{ mx: 2 }}
                />
                <IconButton onClick={handleSendMessage}>
                  <SendIcon />
                </IconButton>
              </Box>
              {showEmojiPicker && (
                <Box sx={{ position: 'absolute', bottom: 0, right: 0 }}>
                  <EmojiPicker
                    onEmojiClick={(emojiObject) => {
                      setMessageInput((prev) => prev + emojiObject.emoji);
                    }}
                  />
                </Box>
              )}
            </Box>
          </>
        ) : (
          <Box
            sx={{
              flexGrow: 1,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <Typography variant="h6" color="text.secondary">
              Chọn một cuộc trò chuyện để bắt đầu
            </Typography>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default Chat;
