import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests if available
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Auth APIs
export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  register: (userData) => api.post('/auth/register', userData),
};

// User APIs
export const userAPI = {
  getProfile: () => api.get('/users/profile'),
  updateProfile: (profileData) => api.put('/users/profile', profileData),
  uploadAvatar: (formData) => api.post('/users/avatar', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  }),
  searchUsers: (query) => api.get(`/users/search?q=${query}`),
};

// Friend APIs
export const friendAPI = {
  getFriends: () => api.get('/friends/list'),
  sendFriendRequest: (userId) => api.post('/friends/request', { userId }),
  acceptFriendRequest: (requestId) => api.post(`/friends/accept/${requestId}`),
  rejectFriendRequest: (requestId) => api.post(`/friends/reject/${requestId}`),
};

// Chat APIs
export const chatAPI = {
  getMessages: (userId) => api.get(`/chat/messages/${userId}`),
  sendMessage: (messageData) => api.post('/chat/messages', messageData),
  uploadFile: (formData) => api.post('/upload', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  }),
};

export default api;
